import Topbar from "./components/topbar/Topbar";
import Homepage from "./pages/homepage/Homepage";
import Login from "./pages/login/Login";
import Register from "./pages/register/Register";
import Settings from "./pages/settings/Settings";
import Single from "./pages/single/Single";
import Write from "./pages/write/Write";
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';

function App() {
  const currentUser = true; // Set this to true or false based on the user's authentication status

  let routeElement;

  if (currentUser) {  // If the user is authenticated (currentUser is true)
    routeElement = <Homepage />;
  } else {
    routeElement = <Register />;
  }

  let writeElement;
  if (currentUser) {
    writeElement = <Write />;
  } else {
    writeElement = <Login />;
  }

  let settingsElement;
  if (currentUser) {
    settingsElement = <Settings />;
  } else {
    settingsElement = <Login />;
  }
  return (
    <Router>
      <Topbar />
      <Routes>
        <Route path="/" element={<Homepage />} />
        <Route path="/posts" element={<Homepage />} />
        <Route path="/register" element={routeElement} />
        <Route path="/login" element={routeElement} />
        <Route path="/post/:id" element={<Single />} />
        <Route path="/write" element={writeElement} />
        <Route path="/settings" element={settingsElement} />
      </Routes>
    </Router>
  );
}

export default App;
